import React, { useState } from 'react';
import { View, Image, StyleSheet } from 'react-native';

export default function App() {
	const questions = [
		{
			questionText: 'Какую патологию ЖКТ вы видите?',
			answerOptions: [
				{ answerText: 'Рак', isCorrect: false },
				{ answerText: 'Полип', isCorrect: false },
				{ answerText: 'Ответ3', isCorrect: true },
				{ answerText: 'Ответ4', isCorrect: false },
			],
		},
		{
			questionText: 'Какую патологию ЖКТ вы видите?',
			answerOptions: [
				{ answerText: 'Рак', isCorrect: false },
				{ answerText: 'Полип', isCorrect: false },
				{ answerText: 'Ответ3', isCorrect: true },
				{ answerText: 'Ответ4', isCorrect: false },
			],
		},
		{
			questionText: 'Какую патологию ЖКТ вы видите?',
			answerOptions: [
				{ answerText: 'Рак', isCorrect: false },
				{ answerText: 'Полип', isCorrect: false },
				{ answerText: 'Ответ3', isCorrect: true },
				{ answerText: 'Ответ4', isCorrect: false },
			],
		},
		{
			questionText: 'Какую патологию ЖКТ вы видите?',
			answerOptions: [
				{ answerText: 'Рак', isCorrect: false },
				{ answerText: 'Полип', isCorrect: false },
				{ answerText: 'Ответ3', isCorrect: true },
				{ answerText: 'Ответ4', isCorrect: false },
			],
		},
	];

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);
	const styles = StyleSheet.create({
		container: {
		  paddingTop: 50,
		},
		tinyLogo: {
		  width: 50,
		  height: 50,
		},
		logo: {
		  width: 66,
		  height: 58,
		},
	  });

	const handleAnswerOptionClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (
		<div className='app'>
			{showScore ? (
				<div className='score-section'>
					Вы набрали {score} очков из {questions.length}. Продолжайте тренироваться!
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Снимок {currentQuestion + 1}</span>/{questions.length}
						</div>
						<View style={styles.container}>
						<Image
							style={styles.tinyLogo}
							source={require('@expo/snack-static/react-native-logo.png')}
						/>
						<Image
							style={styles.tinyLogo}
							source={{
							uri: 'https://reactnative.dev/img/tiny_logo.png',
							}}
						/>
						<Image
							style={styles.logo}
							source={{
							uri: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADMAAAAzCAYAAAA6oTAqAAAAEXRFWHRTb2Z0d2FyZQBwbmdjcnVzaEB1SfMAAABQSURBVGje7dSxCQBACARB+2/ab8BEeQNhFi6WSYzYLYudDQYGBgYGBgYGBgYGBgYGBgZmcvDqYGBgmhivGQYGBgYGBgYGBgYGBgYGBgbmQw+P/eMrC5UTVAAAAABJRU5ErkJggg==',
							}}
						/>
						</View>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}
		</div>
	);
}
